import { useEffect, useState } from 'react';
import './App.css';


function App() {
  const [data, setData] = useState([])
  useEffect(()=>{
    fetch('http://localhost:3000/static/shoes.json')
      .then((response) => response.json())
      .then(setData);
  }, [] )

  if (!data || !data.length) return null;



  return (
    <div className='container'>
      <div className='Logo'>
        <img src='/static/images/super-shoes.png' alt='Super Shoes Logo' />
      </div>
      <div className='carousel'>
        {data.map((item)=>{
          return (
        <div className='item'>
          <div className='image'>
            <img src='https://imgcentauro-a.akamaihd.net/230x230/94313731.jpg' alt='Shoe' />
          </div>
          <div className='info'>
            <span className='name'>Super Shoe1</span>
            <span className='oldPrice'>U$ 299.00</span>
            <span className='price'>U$ 199.00</span>
          </div>
        </div>
         );
          })}
      </div>
    </div>

  );
}

export default App;
